def quarter_of(month):
    if month < 4:
        return 1
    elif month >= 4 and month < 7:
        return 2
    elif month >=7 and month < 10:
        return 3
    elif month >=10 and month <= 12:
        return 4
    else: 
        return "this is not a month number"
